import React, { useState, useEffect, useContext } from "react";
import { Modal } from "react-responsive-modal";
import { useHistory } from "react-router-dom";
import Cart from "./Cart";
import { CourseProvider } from "./CourseContext";
import Login from "./Login";
const NumberContext = React.createContext();
export default function Courses() {
  const [open, setOpen] = useState(false);
  const onOpenModal = () => setOpen(true);
  const onCloseModal = () => setOpen(false);

  const userData = localStorage.getItem("user-data");
  console.log(userData);

  let history = useHistory();
  const [courseData, setCourseData] = useState([]);
  const [cartdata, setcartdata] = useState([]);
  const courseIds = cartdata.map((item) => item.id);
  const isInCart = (id) => courseIds.indexOf(id) !== -1;

  const addToCart = (course) => {
    // if (!userData) {
    //   console.log("Hello")
    //   history.push("/login");
    // }
    setcartdata((prvCourses) => [...prvCourses, course]);
  };

  const deleteToCart = (id) => {
    const newData = cartdata.filter((item) => item.id !== id);
    setcartdata(newData);
  };

  const [searchtext, setsearchtext] = useState('');
  const handleSearch = (e)=> {
    setsearchtext(e.target.value);
  }

  useEffect(() => {
    fetch("data.json", {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((response) => response.json())
      .then((datas) => {
        setCourseData(datas[0].lessons);
      });
  }, []);


  const courses = courseData.map((course, i) => {
    if (i < 5) {
      return (
        <div
          className="card"
          key={course.id}
          style={{ width: "18rem", float: "left" }}
        >
          <img src={course.image} className="card-img-top" alt={course.name} />
          <div className="card-body">
            <p className="card-text">{course.name}</p>

            {userData ? (
              [
                isInCart(course.id) ? (
                  <button onClick={() => deleteToCart(course.id)}>
                    Remove to Cart
                  </button>
                ) : (
                  <button onClick={() => addToCart(course)}>Add to Cart</button>
                ),
              ]
            ) : (
              <button onClick={onOpenModal}>Login</button>
            )}
          </div>
        </div>
      );
    }
  });

  return (
    <div>
      <div>
        <h2>Course Listing</h2>
        <input type="search" name="search" value={state.searchtext} onChange={handleSearch} />
      </div>
      {courses}
      <CourseProvider value={cartdata}>
        <Cart />
      </CourseProvider>

      <Modal
        open={open}
        onClose={onCloseModal}
        center
        classNames={{
          overlay: "customOverlay",
          modal: "customModal",
        }}
      >
        {/* <h2>Simple centered modal</h2> */}
        <Login />
      </Modal>
    </div>
  );
}
