import React, { useState, useEffect, useContext } from "react";
import { Modal } from "react-responsive-modal";
import { useHistory } from "react-router-dom";
import Cart from "./Cart";
import { CourseProvider } from "./CourseContext";
const NumberContext = React.createContext();
export default function Courses() {
  const [open, setOpen] = useState(false);
  const onOpenModal = () => setOpen(true);
  const onCloseModal = () => setOpen(false);

  let history = useHistory();
  const [courseData, setCourseData] = useState([]);
  const [cartdata, setcartdata] = useState([]);
  const courseIds = cartdata.map((item) => (item.id));
  const isInCart = (id) => (courseIds.indexOf(id) !== -1);

  const addToCart = (course) => {
    //console.log(course);
    setcartdata((prvCourses) => [...prvCourses, course]);
   // console.log(cartdata);

    if (!userData) {
      //console.log("Hello")
      history.push("/login");
    }
  };

  const deleteToCart = (id)=> {
    const newData = cartdata.filter((item) => (item.id !== id));
    setcartdata(newData);
  }

  // Call Course API Data Here:
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((datas) => {
        setCourseData(datas);
      });
  }, []);
  const courses = courseData.map((course, i) => {
    if (i < 4) {
      return (
        <div className="card" key={course.id} style={{ width: "18rem", float: "left" }}>
          <img
            src="https://cdn.pixabay.com/photo/2021/02/19/02/50/man-6029064_960_720.jpg"
            className="card-img-top"
            alt={course.title}
          />
          <div className="card-body">
            <p className="card-text">{course.title}</p>
            {
              isInCart(course.id) ? (<button onClick={()=> deleteToCart(course.id) }>Remove to Cart</button>) : (<button onClick={() => addToCart(course)}>Add to Cart</button>)
            }


          </div>
        </div>
      );
    }
  });

  return (
    <div>
      <h2>Course Listing</h2>
      {courses}
      <CourseProvider value={cartdata}>
        <Cart />
      </CourseProvider>

      <Modal open={open} onClose={onCloseModal} center classNames={{
          overlay: 'customOverlay',
          modal: 'customModal',
        }}>
        <h2>Simple centered modal</h2>
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam
          pulvinar risus non risus hendrerit venenatis. Pellentesque sit amet
          hendrerit risus, sed porttitor quam.
        </p>
      </Modal>
    </div>
  );
}
