import React, { useState, useEffect, useContext } from "react";
import { useHistory } from "react-router-dom";
import { CourseProvider } from './CourseContext';
const NumberContext = React.createContext();
export default function Courses() {
  let history = useHistory();
  const [courseData, setCourseData] = useState([]);
  const [cartdata, setcartdata] = useState([]);
  const  addToCart = (course)=> {
    //console.log(name);
    setcartdata(course);
    console.log(cartrdata);
    const userData = JSON.parse(localStorage.getItem('user-data'));
    if(!userData){
      //console.log("Hello")
      history.push('/login')
    }
  }

  // Call Course API Data Here:
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((datas) => {
        setCourseData(datas);
      });
  }, []);
  const courses = courseData.map((course, i) => {
    if (i < 10) {
      return (
        <div className="card" key={i} style={{ width: "18rem", float: "left" }}>
          <img
            src="https://cdn.pixabay.com/photo/2021/02/19/02/50/man-6029064_960_720.jpg"
            className="card-img-top"
            alt={course.title}
          />
          <div className="card-body">
            <p className="card-text">{course.title}</p>
            <button onClick={() => addToCart(course) }>Add to Cart</button>
          </div>
        </div>
      );
    }
  });
  return (
    <div>
      <h2>Course Listing</h2>
      {courses}

    </div>
  );
}
