import React, { useState, useEffect } from "react";

export default function Courses() {
  const [courseData, setCourseData] = useState([]);

  // Call Course API Data Here:
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((datas) => {
        setCourseData(datas);
      });
  }, []);
  const courses = courseData.map((course, i) => {
    return (
      <div className="card" key={i} style="width: 18rem;">
        {course.title}
        <img src="https://cdn.pixabay.com/photo/2021/03/09/14/33/dog-6082017_960_720.jpg" class="card-img-top" alt="..."/>
      </div>
    );
  });
  return (
    <div>
      <h2>Course Listing</h2>
      {courses}
    </div>
  );
}
