import React, { useState, useEffect, useContext } from "react";
import {Provider} from './components/Context'
//const ThemeContext = React.createContext();

export default function Courses() {

    const value = useContext(ThemeContext);

  const [courseData, setCourseData] = useState([]);

  // Call Course API Data Here:
  useEffect(() => {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((response) => response.json())
      .then((datas) => {
        setCourseData(datas);
      });
  }, []);
  const courses = courseData.map((course, i) => {
    if(i < 10 ){
    return ( 
      <div className="card" key={i} style={{ width: "18rem", float: "left" }}>
        <img
          src="https://cdn.pixabay.com/photo/2021/02/19/02/50/man-6029064_960_720.jpg"
          class="card-img-top"
          alt={course.title}
        />
        <div class="card-body">
          <p class="card-text">{course.title}</p>
          <button theme={value}>Add to Cart</button>

        </div>
      </div>

    );
}
  });
  return (
    <div>
      <h2>Course Listing</h2>
      {courses}
    </div>
  );
}
